document.addEventListener("DOMContentLoaded", function () {
  // Slider 1
  var slides1 = document.querySelectorAll(".slider1 .slide");
  var btns1 = document.querySelectorAll(".slider1 .btn");
  let currentSlide1 = 1;

  var manualNav1 = function (manual) {
    slides1.forEach((slide) => {
      slide.classList.remove("active");
    });

    btns1.forEach((btn) => {
      btn.classList.remove("active");
    });

    slides1[manual].classList.add("active");
    btns1[manual].classList.add("active");
  };

  btns1.forEach((btn, i) => {
    btn.addEventListener("click", () => {
      manualNav1(i);
      currentSlide1 = i;
    });
  });

  // Slider 2
  var slides2 = document.querySelectorAll(".slider2 .slide");
  var btns2 = document.querySelectorAll(".slider2 .btn");
  let currentSlide2 = 1;

  var manualNav2 = function (manual) {
    slides2.forEach((slide) => {
      slide.classList.remove("active");
    });

    btns2.forEach((btn) => {
      btn.classList.remove("active");
    });

    slides2[manual].classList.add("active");
    btns2[manual].classList.add("active");
  };

  btns2.forEach((btn, i) => {
    btn.addEventListener("click", () => {
      manualNav2(i);
      currentSlide2 = i;
    });
  });

  // Adjusted autoplay functions
  var repeat1 = function () {
    var repeater = () => {
      setTimeout(function () {
        slides1[currentSlide1].classList.remove("active");
        btns1[currentSlide1].classList.remove("active");

        currentSlide1 = (currentSlide1 + 1) % slides1.length;

        slides1[currentSlide1].classList.add("active");
        btns1[currentSlide1].classList.add("active");

        repeater();
      }, 10000); // Adjust time as needed
    };
    repeater();
  };

  var repeat2 = function () {
    var repeater = () => {
      setTimeout(function () {
        slides2[currentSlide2].classList.remove("active");
        btns2[currentSlide2].classList.remove("active");

        currentSlide2 = (currentSlide2 + 1) % slides2.length;

        slides2[currentSlide2].classList.add("active");
        btns2[currentSlide2].classList.add("active");

        repeater();
      }, 10000); // Adjust time as needed
    };
    repeater();
  };

  repeat1(); // Start autoplay for slider1
  repeat2(); // Start autoplay for slider2
});
